unter++;          }                    if($isAllowedUpdate) {               $tbContent[$i][$counter][TABLE_ISI] = '<a href="'.$editPage.'&id='.$enc->Encode($dataTable[$i]["item_id"]).'"><img hspace="2" width="16" height="16" src="'.$APLICATION_ROOT.'images/b_edit.png" alt="Edit" title="Edit" border="0"></a>';                              $tbContent[$i][$counter][TABLE_ALIGN] = "center";               $counter++;          }                    $tbContent[$i][$counter][TABLE_ISI] = $dataTable[$i]["grup_item_nama"];           $tbContent[$i][$counter][TABLE_ALIGN] = "center";          $counter++;                    $tbContent[$i][$counter][TABLE_ISI] = $dataTable[$i]["item_nama"];           $tbContent[$i][$counter][TABLE_ALIGN] = "center";          $counter++;                    $tbContent[$i][$counter][TABLE_ISI] = currency_format($dataTable[$i]["item_harga_beli"]);           $tbContent[$i][$counter][TABLE_ALIGN] = "right";          $counter++;                    $tbContent[$i][$counter][TABLE_ISI] = currency_format($dataTable[$i]["item_harga_jual"]);           $tbContent[$i][$counter][TABLE_ALIGN] = "right";          $counter++;                    $tbContent[$i][$counter][TABLE_ISI] = $dataTable[$i]["item_jumlah"];          $tbContent[$i][$counter][TABLE_ALIGN] = "center";          $counter++;                    $tbContent[$i][$counter][TABLE_ISI] = $dataTable[$i]["item_stock_warning"];          $tbContent[$i][$counter][TABLE_ALIGN] = "center";          $counter++;                              $tbContent[$i][$counter][TABLE_ISI] = $tipe[$dataTable[$i]["item_tipe"]];          $tbContent[$i][$counter][TABLE_ALIGN] = "center";                    $counter++;                        }          $colspan = count($tbHeader[0]);          if($isAllowedDel) {          $tbBottom[0][0][TABLE_ISI] = '&nbsp;&nbsp;<input type="submit" name="btnDelete" value="Hapus" class="button">&nbsp;';     }          if($isAllowedCreate) {          $t